import React,{Component} from 'react'
import Typography from '@material-ui/core/Typography'
import './../images/tr_green.svg'
import green1 from './../images/tr_green.svg'
import green2 from './../images/bl_green.svg'
import C2CLogo from './c2clogo.js'
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField';

class TeamNumber extends Component{
    state={
        n1:'',
        n2:'',
        n3:'',
        n4:''
    }

    handleChange = name => event => {
        this.setState({
          [name]: event.target.value,
        });
    };

    go=()=>{
        if(this.state.a1 && this.state.a2 && this.state.a3 && this.state.a4){
            let s=this.state.a1 + this.state.a2 + this.state.a3 + this.state.a4
            localStorage.setItem('c2c_judge_teamno',s)
            window.location.href="/team_eval"
        }
    }

    shiftFocus=()=>{
        if(!this.state.n2){
            document.querySelector('#num2').focus()
        }
        else if(!this.state.n3){
            document.querySelector('#num3').focus()
        }
        else if(!this.state.n4){
            document.querySelector('#num4').focus()
        }
    }

    logout=()=>{
        localStorage.clear()
        window.location.href="/login"
    }

    render(){
        this.shiftFocus()
        console.log(document.querySelector('#num2'))
        return(
            <div className="team_number_div center">
                {/* <img className="green1" src={green1} />
                <img className="green2" src={green2} /> */}

                <C2CLogo />

                <Typography component="p" className="enter_tnum">Enter Team Number</Typography>

                <input type="text" className="num_inp" id="num1" onChange={this.handleChange('n1')} autoFocus/>
                <input type="text" className="num_inp" id="num2" onChange={this.handleChange('n2')}/>
                <input type="text" className="num_inp" id="num3" onChange={this.handleChange('n3')}/>
                <input type="text" className="num_inp" id="num4" onChange={this.handleChange('n4')}/>
                <br></br>
                <Button onClick={this.go} className="team_number_submit">SUBMIT</Button>

                <Typography component="p" className="welcome_name">Welcome <span className="bold">{localStorage.getItem('c2c_judge_auth')}</span></Typography>
                <Typography component="p" onClick={this.logout} className="logout_link">Logout</Typography>

            </div>
        )
    }
}

export default TeamNumber
